var viewport = require('../'),
    test = require('tap').test;

test('viewport', function(t) {

    t.deepEqual(viewport.viewport([
        5.668343999999995,
        45.111511000000014,
        5.852471999999996,
        45.26800200000002
    ], [640, 480]), {
        center: [
            5.7604079999999955,
            45.189756500000016
        ],
        zoom: 11
    });

    t.deepEqual(viewport.viewport([
        5.668343999999995,
        45.111511000000014,
        5.852471999999996,
        45.26800200000002
    ], [64, 48]), {
        center: [
            5.7604079999999955,
            45.189756500000016
        ],
        zoom: 8
    });

    t.deepEqual(viewport.viewport([
        5.668343999999995,
        45.111511000000014,
        5.852471999999996,
        45.26800200000002
    ], [10, 10]), {
        center: [
            5.7604079999999955,
            45.189756500000016
        ],
        zoom: 5
    });

    t.end();
});

test('bounds for 512px tiles', function(t) {
    t.deepEqual(
        viewport.bounds([-77.036556, 38.897708], 17, [1080, 350], 512),
        [-77.03945338726044, 38.89697827424865, -77.03365981578827, 38.89843950894583]
    );

    t.end();
});
